import React, { useState, useEffect } from "react";
import "./index.scss";

import ChartSolides from "../../../../../components/charts";

const SituationalIndicators = ({ data }) => {
    const [nivelenergia, setNivelenergia] = useState("");
    const [nivelestress, setNivelestress] = useState("");
    const [ia_nivel, setIa_nivel] = useState("");
    const [moral_nivel, setMoral_nivel] = useState("");
    const [nivelip, setNivelip] = useState("");
    const [amplitude_nivel, setAmplitude_nivel] = useState("");
    const [niveltempo, setNiveltempo] = useState("");
    const [flex_nivel, setFlex_nivel] = useState("");
    const [nivel_incitab, setNivel_incitab] = useState("");
    const [nivel_energperfil, setNivel_energperfil] = useState("");

    console.log(data);

    useEffect(() => {
        switch (data.nivelenergia) {
            case "Muito Baixa":
                setNivelenergia(1);
                break;
            case "Baixa":
                setNivelenergia(2);
                break;
            case "Norm Baixa":
                setNivelenergia(3);
                break;
            case "Normal":
                setNivelenergia(4);
                break;
            case "Norm alta":
                setNivelenergia(5);
                break;
            case "Alta":
