                break;
            case "Norm alta":
                setIa_nivel(5);
                break;
            case "Alta":
                setIa_nivel(6);
                break;
            case "Muito Alta":
                setIa_nivel(7);
                break;

            default:
                break;
        }
        switch (data.moral_nivel) {
            case "Muito Baixa":
                setMoral_nivel(1);
                break;
            case "Baixa":
                setMoral_nivel(2);
                break;
            case "Norm Baixa":
                setMoral_nivel(3);
                break;
            case "Normal":
                setMoral_nivel(4);
                break;
            case "Norm alta":
                setMoral_nivel(5);
                break;
            case "Alta":
                setMoral_nivel(6);
                break;
            case "Muito Alta":
                setMoral_nivel(7);
                break;

            default:
