        }
        switch (data.nivelip) {
            case "Muito Baixa":
                setNivelip(1);
                break;
            case "Baixa":
                setNivelip(2);
                break;
            case "Norm Baixa":
                setNivelip(3);
                break;
            case "Normal":
                setNivelip(4);
                break;
            case "Norm alta":
                setNivelip(5);
                break;
            case "Alta":
                setNivelip(6);
                break;
            case "Muito Alta":
                setNivelip(7);
                break;

