                setAmplitude_nivel(7);
                break;

            default:
                break;
        }
        switch (data.niveltempo) {
            case "Muito Baixa":
                setNiveltempo(1);
                break;
            case "Baixa":
                setNiveltempo(2);
                break;
            case "Norm Baixa":
                setNiveltempo(3);
                break;
