                break;
            case "Muito Alta":
                setNivel_energperfil(7);
                break;

            default:
                break;
        }
    }, [data]);

    return (
        <div className="situational-indicators-noAuth">
            <h2>Indicadores situacionais</h2>
            <div className="row-situational">
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.nivelenergia,
                        percentual: nivelenergia
                    }}
                />
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.nivelestress,
                        percentual: nivelestress
                    }}
                />
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.ia_nivel,
                        percentual: ia_nivel
