                    }}
                />
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.niveltempo,
                        percentual: niveltempo
                    }}
                />
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.flex_nivel,
                        percentual: flex_nivel
                    }}
                />
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.nivel_incitab,
                        percentual: nivel_incitab
                    }}
                />
                <ChartSolides
                    type="situationalIndicator"
                    situationalIndicator={{
                        description: data.nivel_energperfil,
                        percentual: nivel_energperfil
                    }}
                />
            </div>
        </div>
    );
};

export default SituationalIndicators;
